const navToggleIcon=document.querySelector(".nav__btn--menu"),menu=document.querySelector(".nav__menu"),cover=document.querySelector(".cover"),panelsName=document.querySelectorAll(".panels__name__item");function navigationTabsInit(e,t,n){e.forEach(e=>{e.addEventListener("click",function(){removeActiveClass(t),removeActiveClass(n),this.classList.add(t);let e=this.getAttribute("data-content-id");document.querySelector(e).classList.add(n)})})}function removeActiveClass(e){document.querySelector(`.${e}`).classList.remove(e)}navToggleIcon.addEventListener("click",function(){this.classList.toggle("nav__btn--menu--active"),menu.classList.toggle("nav__menu--show"),cover.classList.toggle("cover--show")}),navigationTabsInit(panelsName,"panels__name__item--active","panels__item--show");